	speak("archer",_("Take a look around. That stuff has to be worth a fortune!"),"excited");
else
	speak("archer",_("That stuff must be worth a fortune!"),"excited");
end;
if (archer ~= nil) then
	speak("warrior",_("This junk?"),"bored");
end;
if (leader ~= mage) then
	speak("mage",_("Philistine."),"unhappy");
end;

